import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-change-chart-font-style',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './change-chart-font-style.component.html',
})
export class ChangeChartFontStyleComponent {
  options: any = {
    display: { title: false },
    theme: { font: { fontFamily: 'Arial', fontSize: 16 } },
  };
  fontFamily = 'Arial';

  changeFontFamily(event?: any) {
    this.fontFamily = event.target?.value;
    this.options.theme.font.fontFamily = this.fontFamily;
  }
}
