import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-change-chart-property',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './change-chart-property.component.html'
})
export class ChangeChartPropertyComponent {
  options: any = {
    display: {
      title: false,
      categoryLabels: true,
      values: 'absolute'
    }
  };

  changeValuesOption(event?: any) {
    this.options.display.values = event.target?.value;
  }
}
