import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent, ItemType } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-change-chart-type',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './change-chart-type.component.html'
})
export class ChangeChartTypeComponent {
  chartType: ItemType = 'bar-chart';

  changeChartType(event?: any) {
    this.chartType = event.target?.value;
  }
}
