import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-multi-lingual-chart',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './multi-lingual-chart.component.html'
})
export class MultiLingualChartComponent {
  chartTitle = {
    en: 'Donut chart with sample data',
    nl: 'Donut chart met voorbeeldgegevens',
    fr: 'Graphique en anneau avec des exemples de donn√©es',
  }

  options = {
    title: this.chartTitle,
    locale: 'en'
  };

  changeLanguage(event: any) {
    this.options = {
      ...this.options, locale: event.target.value
    }
  }
}
