import { Component, ElementRef, viewChild } from '@angular/core';
import { JsonPipe } from '@angular/common';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-retrieve-chart-filters',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent, JsonPipe],
  templateUrl: './retrieve-chart-filters.component.html'
})
export class RetrieveChartFiltersComponent {
  chart = viewChild<any>('chart');
  filters: any;

  getFilters() {
    this.filters = this.chart().getFilters();
  }
}
