customFiles.scss:
.d-none {
  display: none;
}
.d-block {
  display: block;
}
luzmo-viz-item {
  border: 1px solid rgba(0,0,0,.125);
  border-radius: .5rem;
  margin-bottom: 1rem;
}
.main-numbers luzmo-viz-item {
  height: 100px;
}
.charts luzmo-viz-item {
  height: 300px;
}

// Medium devices (tablets, 768px and up)
@media (min-width: 992px) {
  .main-numbers luzmo-viz-item {
    height: 200px;
  }
  .charts luzmo-viz-item {
    height: 400px;
  }
}

// X-Large devices (large desktops, 1200px and up)
@media (min-width: 1200px) {
  .charts, .main-numbers {
    display: flex;
    gap: 1rem;
  }
  .d-lg-flex {
    display: flex;
  }
  .d-lg-none {
    display: none;
  }
}

