import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-apply-custom-theme',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent, CommonModule],
  templateUrl: './apply-custom-theme.component.html',
})
export class ApplyCustomThemeComponent {
  options: any;
  darkTheme = {
    itemsBackground: 'black',
    colors: [
      '#FFB74D',
      '#FFEB3B',
      '#FF4081',
      '#40C4FF',
      '#66BB6A',
      '#BA68C8',
      '#FF5252',
      '#4DD0E1',
    ],
    font: { fontFamily: 'Roboto', fontSize: 16 },
  };
  lightTheme = {
    itemsBackground: 'white',
    colors: [
      '#FF5733',
      '#FFC300',
      '#FF33FF',
      '#33CCFF',
      '#33FF77',
      '#9966FF',
      '#FF66CC',
      '#66FFCC',
    ],
    font: { fontFamily: 'Lato', fontSize: 17 },
  };

  constructor() {
    this.options = { title: { en: 'Dark theme' }, theme: this.darkTheme };
  }

  setTheme(event: any) {
    if (event.target.value === 'dark') {
      this.options.theme = this.darkTheme;
      this.options.title.en = 'Dark theme';
    }
    else {
      this.options.theme = this.lightTheme;
      this.options.title.en = 'Light theme';
    }
  }
}
