import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent, Slots } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-change-chart-data',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './change-chart-data.component.html',
})
export class ChangeChartDataComponent {
  /* IMPORTANT
    The hardcoded authKey and authToken are for demonstration purposes only.
    You should always have a backend service generating authKey & token for you providing the access to your dashboard, chart and/or datasets.
    More info here: https://developer.luzmo.com/api/createAuthorization
  */
  authKey = 'e358db61-6435-48d7-8310-99ce606cc8d4';
  authToken = 'DU7Cnb4rRNh2FSx1Ac1bbuLXQpK8oLRS8Wcl5ymc6wAoaxh4fry2M52Kq71nGbAyD3pCAGm6auL1AG3qSPnX9uYKTdZzf4tezOXXJMQqtOhV3ElFLioXMtQzrZN074M91RLfMgKDWkAQRBMN4HhZbm';
  slots: Slots = [];
  options = {};

  dataConfigs = [
    {
      title: 'Volumes sold last 6 months',
      datasetId: 'f0230468-7d0b-448d-81d6-d1baf0a8cfa3',
      measureColumnId: 'f05f582a-032d-433f-87c4-1bf1c349e784',
      measureLabel: 'close',
      dateColumnId: '073e5cb0-b843-42a2-ba88-6841578a6ab6',
      dateLabel: 'date',
    },
    {
      title: 'Stock price last 5 years',
      datasetId: '9868c126-27de-42c0-98b4-b7d2db5cf39f',
      measureColumnId: 'd2bba580-bc39-4293-a120-0300a16b5f50',
      measureLabel: 'close',
      dateColumnId: '0327ac84-ddb6-4f73-94d8-4c459c2b04d8',
      dateLabel: 'date',
    },
  ];

  changeChartData(event?: any) {
    const index = parseInt(event?.target?.value ?? 0, 10);
    this.slots = [
      {
        name: 'measure',
        content: [
          {
            set: this.dataConfigs[index].datasetId,
            column: this.dataConfigs[index].measureColumnId,
            label: {
              en: this.dataConfigs[index].measureLabel,
            },
            type: 'numeric',
            format: '.4f',
          },
        ],
      },
      {
        name: 'x-axis',
        content: [
          {
            set: this.dataConfigs[index].datasetId,
            column: this.dataConfigs[index].dateColumnId,
            label: {
              en: this.dataConfigs[index].dateLabel,
            },
            type: 'datetime',
            format: '%d-%m-%Y %H:%M:%S.%L',
            lowestLevel: 9,
            level: 7,
          },
        ],
      },
    ];

    this.options = { display: { title: false } };
  }

  constructor() {
    this.changeChartData();
  }
}
