import { Component } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-change-chart-theme',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './change-chart-theme.component.html'
})
export class ChangeChartThemeComponent {
  options = { theme: 'default' };

  changeChartTheme(event?: any) {
    this.options.theme = event.target?.value;
  }
}
