import { Component, ElementRef, OnInit, viewChild } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-chart-css-overides',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './chart-css-overrides.component.html',
  styleUrl: './chart-css-overrides.component.scss'
})
export class ChartCSSOverridesComponent implements OnInit{
  chart = viewChild<any>('chart');

  options: any = {
    display: { title: false }
  };

  ngOnInit() {
    this.addStyleToShadowDom();
  }

  addStyleToShadowDom() {
    const style = document.createElement( 'style' );
    style.innerHTML = '.widget-wrapper { background-color: red !important; }';

    // doesn't work
    this.chart().elementRef.nativeElement.shadowRoot?.appendChild( style );
  }
}
