import { Component, ElementRef, viewChild } from '@angular/core';
import { NgxLuzmoVizItemComponent } from '@luzmo/ngx-embed';

@Component({
  selector: 'app-chart-export',
  standalone: true,
  imports: [NgxLuzmoVizItemComponent],
  templateUrl: './chart-export.component.html'
})
export class ChartExportComponent {
  chart = viewChild<any>('chart');

  export() {
    this.chart().export('png');
  }
}
